INSERT INTO myapp.news (id, version, tieu_de, is_deleted, ngay_tao, last_modified_user, noi_dung_chi_tiet, hinh_anh, noi_dung_ngan, last_modified_time) VALUES ('6af5da51-3e6b-43fe-ab9d-36bb473cb710', 0, 'Xem camera giám sát qua điện thoại', false, '2018-03-09', 'admin', '<h1 class="header2">Xem camera quan s&aacute;t qua m&aacute;y t&iacute;nh</h1>
<div class="news-view">&nbsp;</div>
<ul class="hightlight#_news">
<li><a href="http://fptcamera.vn/tin-tuc/xem-camera-giam-sat-qua-dien-thoai"><em class="fa fa-angle-right">&nbsp;</em>Xem camera gi&aacute;m s&aacute;t qua điện thoại</a></li>
<li><a href="http://fptcamera.vn/tin-tuc/xem-camera-qua-dien-thoai-bang-phan-mem-vmeyesuper"><em class="fa fa-angle-right">&nbsp;</em>Xem camera qua điện thoại bằng phần mềm vMEyesuper</a></li>
</ul>
<div class="clear">&nbsp;</div>
<div class="news-desc">Ng&agrave;y nay camera quan s&aacute;t được sử dụng kh&aacute; phổ biến trong c&ocirc;ng việc v&agrave; cuộc sống thường ng&agrave;y, C&aacute;c cửa h&agrave;ng lắp camera gi&aacute;m s&aacute;t nhằm quản l&yacute; nh&acirc;n vi&ecirc;n, quản l&yacute; kh&aacute;ch ra v&agrave;o, C&aacute;c gia đ&igrave;nh lắp camera bảo vệ an ninh hay c&aacute;c doanh nghiệp với mục đ&iacute;ch gi&aacute;m s&aacute;t nh&acirc;n vi&ecirc;n l&agrave;m việc.</div>
<div class="news-body">
<h2><span style="color: ##0000cd;"><span style="font-size: 20px;"><strong>Hướng dẫn xem camera quan s&aacute;t qua m&aacute;y t&iacute;nh từ xa</strong></span></span></h2>
<p><strong><span style="color: ##ff0000;">Đ&aacute;p ứng nhu cầu của người sử dụng</span></strong>&nbsp;c&aacute;c loại camera th&ocirc;ng minh ứng dụng c&ocirc;ng nghệ hiện đại&nbsp;được ra đời với nhiều t&iacute;nh năng sử dụng hơn như: Camera quay 360 độ, camera ngụy trang si&ecirc;u nhỏ, Camera ip wifi xem qua điện thoại....&nbsp;</p>
<p><span style="color: ##ff0000;"><strong>FPT Security Việt Nam</strong></span> chuy&ecirc;n cung cấp c&aacute;c loại <em><strong><a href="http://fptcamera.vn/camera-giam-sat">camera gi&aacute;m s&aacute;t uy t&iacute;n</a></strong></em>, gi&aacute; cả cạnh tranh. Để gi&uacute;p qu&yacute; kh&aacute;ch dễ d&agrave;ng sử dụng tự m&igrave;nh l&agrave;m chủ hệ thống camera của m&igrave;nh, ch&uacute;ng t&ocirc;i xin được hướng dẫn qu&yacute; kh&aacute;ch h&agrave;ng <strong>xem camera tr&ecirc;n m&aacute;y t&iacute;nh</strong> qua Internet với v&agrave;i thao t&aacute;c đơn giản.</p>
<p style="text-align: center;">&nbsp;</p>
<p>Mỗi h&atilde;ng camera đều c&oacute; phần mềm chuy&ecirc;n dụng để hỗ trợ việc xem từ xa qua c&aacute;c thiết bị kết nối mạng, qu&yacute; kh&aacute;ch t&ugrave;y v&agrave;o loại camera m&igrave;nh sử dụng m&agrave; xem hướng dẫn c&aacute;c b&agrave;i viết dưới đ&acirc;y cho hợp l&yacute;:</p>
<p><a href="http://fptcamera.vn/tin-tuc/huong-dan-xem-camera-bang-phan-mem-ivms-4200"><strong>Hướng dẫn xem camera qua m&aacute;y t&iacute;nh h&atilde;ng HIKVISION</strong></a></p>
<p><strong><a href="http://fptcamera.vn/tin-tuc/huong-dan-xem-camera-tren-may-tinh-bang-cms">Hướng dẫn xem camera qua m&aacute;y t&iacute;nh h&atilde;ng BENCO, HDPRO</a></strong></p>
<p><strong>Hướng dẫn xem camera tr&ecirc;n m&aacute;y t&iacute;nh h&atilde;ng DAHUA</strong></p>
</div>', 'http://fptcamera.vn/resources/uploads/images/automatic/tin-tuc/tu-van-lap-dat-camera-quan-sat.jpg', 'Đáp ứng nhu cầu sử dụng sau khi lắp đặt camera của FPT Việt Nam nếu có sảy ra lỗi quý khách chỉ cần sử dụng TeamViewer để chúng tôi có thể điều khiển máy tính từ xa để hỗ trợ quý khách chỉ trong vài giây. Chỉ tải về và dùng ngay!', '2018-03-09 08:56:07');
